import React, { useState } from 'react';
import './ImageCarousel.css'; // Ensure your CSS styles are imported

const ImageCarousel = ({ images }) => {
  const [current, setCurrent] = useState(0);

  const nextSlide = () => {
    // Check if we're at the last image, if so, reset to 0, otherwise increment
    setCurrent(current => (current < images.length - 1 ? current + 1 : current));
  };

  const prevSlide = () => {
    // Check if we're at the first image, if so, stay there, otherwise decrement
    setCurrent(current => (current > 0 ? current - 1 : 0));
  };

  return (
    <div className="carousel-container">
      <h2>Gallery</h2>
      <div className="carousel-wrapper">
        <button className="left-arrow" onClick={prevSlide} disabled={current === 0}>&lsaquo;</button>
        <div className="carousel-content-wrapper">
          <div className="carousel-content" style={{ transform: `translateX(-${current * 100}%)` }}>
            {images.map((image, index) => (
              <img src={image} alt={`Slide ${index}`} key={index} className="carousel-image" />
            ))}
          </div>
        </div>
        <button className="right-arrow" onClick={nextSlide} disabled={current === images.length - 1}>&rsaquo;</button>
      </div>
    </div>
  );
};

export default ImageCarousel;
