import React, { useState, useEffect } from 'react';
import './ImageCarousel.css';

const ImageCarousel = ({ images }) => {
  // Add two more states to control the transition
  const [current, setCurrent] = useState(0);
  const [isTransitioning, setIsTransitioning] = useState(true);
  const length = images.length;

  // Additional function to handle the infinite transition
  const moveToSlide = (index) => {
    setIsTransitioning(true);
    setCurrent(index);
  };

  useEffect(() => {
    if (current === 0 || current === length - 1) {
      const transitionTimeout = setTimeout(() => {
        setIsTransitioning(false);
      }, 500); // Corresponds to the CSS transition duration
      return () => clearTimeout(transitionTimeout);
    }
  }, [current, length]);

  const nextSlide = () => {
    if (current === length - 1) {
      // If we're at the end, "jump" to the first slide without transition
      setIsTransitioning(false);
      setCurrent(0);
    } else {
      // Otherwise, move to the next slide normally
      moveToSlide(current + 1);
    }
  };

  const prevSlide = () => {
    if (current === 0) {
      // If we're at the beginning, "jump" to the last slide without transition
      setIsTransitioning(false);
      setCurrent(length - 1);
    } else {
      // Otherwise, move to the previous slide normally
      moveToSlide(current - 1);
    }
  };

  return (
    <div className="carousel-container">
      <h2>Gallery</h2>
      <button className="left-arrow" onClick={prevSlide}>&lsaquo;</button>
      <div className="carousel-content-wrapper">
        <div
          className={`carousel-content ${isTransitioning ? 'transitioning' : ''}`}
          style={{ transform: `translateX(-${current * 100}%)` }}
        >
          {images.map((image, index) => (
            <img src={image} alt={`Slide ${index}`} key={index} className="carousel-image" />
          ))}
        </div>
      </div>
      <button className="right-arrow" onClick={nextSlide}>&rsaquo;</button>
    </div>
  );
};

export default ImageCarousel;
