import React, { useState } from 'react';
import './ImageCarousel.css'; // Ensure your CSS styles are imported.

const ImageCarousel = ({ images }) => {
  const [current, setCurrent] = useState(0);
  const length = images.length;

  // Consider the number of images to show at once.
  const visibleImagesCount = 3;

  const nextSlide = () => {
    setCurrent((current + 1) % length);
  };

  const prevSlide = () => {
    setCurrent(current === 0 ? length - 1 : current - 1);
  };

  if (!Array.isArray(images) || images.length <= 0) {
    return null;
  }

  return (
    <div className="carousel-container">
      <button className="left-arrow" onClick={prevSlide}>⟨</button>

      {/* This container will hold the visible slides */}
      <div className="carousel-inner">
        {images.map((image, index) => (
          <div 
            className={index >= current && index < current + visibleImagesCount ? 'slide active' : 'slide'}
            key={index}
          >
            {/* Conditionally rendering the image if it's in the range of the current slide */}
            {index >= current && index < current + visibleImagesCount && (
              <img src={image} alt={`Slide ${index}`} className="image" />
            )}
          </div>
        ))}
      </div>

      <button className="right-arrow" onClick={nextSlide}>⟩</button>

      {/* Indicators */}
      <div className="carousel-indicators">
        {images.map((_, idx) => (
          <span 
            key={idx} 
            className={idx === current ? 'dot active' : 'dot'}
            onClick={() => setCurrent(idx)}
          ></span>
        ))}
      </div>
    </div>
  );
};

export default ImageCarousel;
