import React, { useState } from 'react';
import './ImageCarousel.css'; // Make sure to import the CSS styles

const ImageCarousel = ({ images }) => {
  const [current, setCurrent] = useState(0);

  const length = images.length;

  const nextSlide = () => {
    setCurrent(current => (current === length - 1 ? 0 : current + 1));
  };

  const prevSlide = () => {
    setCurrent(current => (current === 0 ? length - 1 : current - 1));
  };

  if (!Array.isArray(images) || images.length <= 0) {
    return null;
  }

  return (
    <div className="carousel-container">
      <h2>Gallery</h2>
      <div className="carousel-wrapper">
        <button className="left-arrow" onClick={prevSlide}>&lsaquo;</button>
        <div className="carousel-content-wrapper">
          <div className="carousel-content" 
               style={{ 
                 transform: `translateX(-${current * 100}%)`, 
                 transition: current === 0 || current === length - 1 ? 'none' : 'transform 0.5s ease' 
               }}>
            {current === length - 1 && (
              // Clone of last image to show when transitioning from first to last
              <img src={images[length - 1]} alt={`Slide ${length - 1}`} className="carousel-image" />
            )}
            {images.map((image, index) => (
              <img src={image} alt={`Slide ${index}`} key={index} className="carousel-image" />
            ))}
            {current === 0 && (
              // Clone of first image to show when transitioning from last to first
              <img src={images[0]} alt={`Slide 0`} className="carousel-image" />
            )}
          </div>
        </div>
        <button className="right-arrow" onClick={nextSlide}>&rsaquo;</button>
      </div>
    </div>
  );
};

export default ImageCarousel;
